import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-navigation',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <!-- MOBILE -->
    <footer
      class="footer mobile-footer"
      *ngIf="!isDesktop">

      <div class="row">
        <a class="col" href="#">Home</a>
        <a class="col" href="#">Impressum</a>
        <a class="col" href="#">Datenschutz</a>

        <ng-container *ngIf="!isLoggedIn">
          <a class="col" href="#">Login</a>
        </ng-container>

        <ng-container *ngIf="isLoggedIn">
          <a class="col" href="#">Admin</a>
          <button class="logout-button col" (click)="logout()">Logout</button>
        </ng-container>

        <select class="col" [(ngModel)]="selectedLanguage">
          <option value="de">DE</option>
          <option value="en">EN</option>
        </select>
      </div>
    </footer>

    <!-- DESKTOP -->
    <footer
      class="footer desktop-footer"
      *ngIf="isDesktop">

      <a href="#">Home</a>
      <a href="#">Impressum</a>
      <a href="#">Datenschutz</a>

      <ng-container *ngIf="!isLoggedIn">
        <a href="#">Login</a>
      </ng-container>

      <ng-container *ngIf="isLoggedIn">
        <a href="#">Admin</a>
        <button class="logout-button" (click)="logout()">Logout</button>
      </ng-container>

      <select [(ngModel)]="selectedLanguage">
        <option value="de">Deutsch</option>
        <option value="en">English</option>
      </select>
    </footer>
  `,
  styles: [`
    .footer {
      position: fixed;
      bottom: 0;
      width: 100%;
      background: #f8f9fa;
      padding: 12px;
      display: flex;
      justify-content: space-around;
      align-items: center;
      font-size: 14px;
      left: 0;
    }

    .mobile-footer .row {
      width: 100%;
      margin: 0;
    }

    a {
      text-decoration: none;
      color: #979C9E;
    }

    a:hover {
      color: #76B900;
    }

    select {
      border: none;
      background: none;
      cursor: pointer;
      color: #979C9E;
    }

    .logout-button {
      background: none;
      border: none;
      color: #979C9E;
      cursor: pointer;
    }

    .logout-button:hover {
      color: #76B900;
    }
  `]
})
export class NavigationComponent {

  /** Storybook steuerbare States */
  @Input() isLoggedIn = false;
  @Input() isDesktop = false;

  selectedLanguage: 'de' | 'en' = 'de';

  logout() {
    console.log('Logout clicked');
  }
}

